	const connectWebsocket = (url) => {
		socket = new WebSocket(url)

		socket.onclose = ev => {
			socket.send(JSON.stringify({
				"type": "4",
				"data": "reconnect"
			}));
		}

		socket.onopen = ev => {
			heartbeat()
		}

		return socket
	}

	const heartbeat = () => {
		if (!socket) return

		if (socket.readyState !== 1) return

		socket.send(JSON.stringify({
			"type": "2",
			"data": "pong"
		}));

		setTimeout(heartbeat, 1000);
	}

