				let checkKey = true;
				const form = $this.closest('form');
				const regName = /^[a-zA-Z\s]+$/;
				const regTel = /^[0-9]{7,32}$/;
				const regEmail = /^[A-Za-z0-9]+[A-Za-z0-9_\-\.\+]*\@[A-Za-z0-9\-\+]*[A-Za-z0-9]+(\.[A-Za-z0-9\-]+)+$/;
				const recaptchaWidget = recaptchaWidgets[form.find('.g-recaptcha').attr('id')]

				const _this = $this.parents('.tm-ao-form-container');

				_this.find('.form-list input').each(function (index, el) {
					const _name = $(el).attr('name');
					const _val = $(el).val();
					let errorKey;

					// Check required input.
					if (_name) {
						if (_name !== 'middleName' && !_val) {
							switch (_name) {
								case 'firstName':
									errorKey = 'First Name';
									break;
								case 'lastName':
									errorKey = 'Last Name';
									break;
								case 'country':
									errorKey = 'Country';
									break;
								case 'tel':
									errorKey = 'Mobile';
									break;
								case 'email':
									errorKey = 'Email';
									break;
							}
							formTipsContent('' + errorKey + ' cannot be empty.');
							checkKey = false;
							return false;
						}

						// Validate names (first name, middle name, last name).
						if (_name === 'firstName' || _name === 'middleName' || _name === 'lastName') {
							if (_val && !regName.test(_val)) {
								formTipsContent('Please enter valid name.');
								checkKey = false;
								return false;
							}
						}
						// Validate mobile number.
