					if (grecaptchaResponse.length === 0) {
						formTipsContent('Please perform Google verification.');

						checkKey = false;
						return false;
					}
				}

				// If validation passes, build params.
				if (checkKey) {
					// Add English country name for request param
					$.each(countryEnData, function (i, v) {
						if (v.classname === _this.find('[name="country"]').attr('country-code')) {
							_this.find('[name="country"]').attr('countryEn', v.country);
