						? REFERRAL_CODE
						: ''

					// Set sendEmail param
					const sendEmail = (typeof set_SendEmail !== 'undefined' && set_SendEmail !== "") ? parseInt(set_SendEmail) : 0;

					params = {
						first_name: _this.find('[name="firstName"]').val(),
						middle_name: _this.find('[name="middleName"]').val(),
						last_name: _this.find('[name="lastName"]').val(),
						country: _this.find('[name="country"]').attr('countryEn'),
						mobile: _this.find('[name="tel"]').val(),
						email: _this.find('[name="email"]').val().toLowerCase(),
						country_code: _this.find('.tm-tel-text.arrow').text().trim(),
