					// Alert loading process dialog
					Swal.fire({
						text: 'Signing you up ...',
						showConfirmButton: false,
						toast: true,
						target: form.get(0),
						didOpen() {
							Swal.showLoading()
						}
					})

					const requestUrl = '' ? '/register' : 'https://www.tmgm.com/en/register'
						console.log(requestUrl)
					$.post(requestUrl, params, function (res) {
						const data = JSON.parse(res);
						if (data) {
							if (!data.message) {
								const _code = data.redirect_address.split('sign=');
								const _url = encodeURIComponent(_code[1]);
								if (data.redirect_address && $(window).width() > 1024) window.open(_code[0] + 'sign=' + _url + '&language=en');
