										break;
									case 80051:
										formTipsContent("The email has already been taken");
										break;
									case 40018:
										formTipsContent("We do not provide service to your selected country");
										break;
									case 41009:
										formTipsContent("Referral code error");
										break;
									case 41040:
										formTipsContent("User registration failed");
										break;
									case 80003:
